// page init
jQuery(function(){
	initCarousel();
	initSameHeight();
	initSlideHeader();
});

function initSlideHeader(){
	var animSpeed = 700;
	var win = jQuery(window);
	var fixedClass = 'fixed';
	jQuery('#header.slideit').each(function(){
		var box = jQuery(this);
		function slideHeader(){
			if(win.scrollTop() > box.outerHeight()){
				if(!box.hasClass(fixedClass)){
					box.hide().addClass(fixedClass).slideDown(animSpeed);
				}
			}
			else if(win.scrollTop() === 0){
				box.removeClass(fixedClass);
			}
		}
		win.bind('load scroll',slideHeader);
	})
}

// scroll gallery init
function initCarousel() {
	var isTouchDevice = (/MSIE 10.*Touch/.test(navigator.userAgent)) || ('ontouchstart' in window) || window.DocumentTouch && document instanceof DocumentTouch;
	var timeout = 2000;
	jQuery('div.carousel').scrollGallery({
		mask: 'div.mask',
		slider: 'div.slideset',
		slides: 'div.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		onInit: function(that){
			if(!isTouchDevice){
				var flag = false;
				var timer;
				var leftSideClass = 'left-arrow';
				var rightSideClass = 'right-arrow';
				var visibleClass = 'visible';
				var firstTime = 'both-arrows';
				
				function addClass(e){
					if(e.pageX < that.mask.offset().left + that.mask.outerWidth() / 2){
						that.gallery.removeClass(rightSideClass).addClass(leftSideClass);
					}
					else{
						that.gallery.removeClass(leftSideClass).addClass(rightSideClass);
					}
				}
				
				that.mask.bind('mousemove',function(e){
					if(flag) addClass(e);
				}).hover(function(e){
					clearTimeout(timer);
					//timer = setTimeout(function(){
						if(!flag) that.gallery.addClass(firstTime);
						else that.gallery.addClass(visibleClass);
					//},timeout)
				},function(){
					clearTimeout(timer);
					timer = setTimeout(function(){
						flag = true;
						that.gallery.removeClass(visibleClass).removeClass(firstTime);
					},timeout)
				})
				
				that.btnNext.add(that.btnPrev).bind('mouseenter',function(){
					clearTimeout(timer);
				}).bind('mouseleave',function(){
					timer = setTimeout(function(){
						flag = true;
						that.gallery.removeClass(visibleClass).removeClass(firstTime);
					},timeout)
				}).bind('click',function(){
					that.stopRotation();
				})
				
				that.pagerLinks.bind('click',function(){
					that.stopRotation();
				})
			}
		},
		pagerLinks: '.pagination li',
		maskAutoSize: false,
		autoRotation: true,
		switchTime: 2000,
		animSpeed: 500,
		step: 1
	});
}

// align blocks height
function initSameHeight() {
	jQuery('.vendors-block').sameHeight({
		elements: '.box',
		multiLine: true
	});
	jQuery('.vendors-block').sameHeight({
		elements: '.img',
		multiLine: true,
		biggestHeight: true
	});
}

